JavaScript for validation and submission:
```javascript
document.getElementById('goal-form').addEventListener('submit', function(e) {
  e.preventDefault();
  const nameInput = document.getElementById('goal-name');
  const categoryInput = document.getElementById('goal-category');
  const targetInput = document.getElementById('goal-target');
  const dateInput = document.getElementById('goal-date');

  // Reset errors
  document.querySelectorAll('.text-red-500').forEach(el => el.classList.add('hidden'));

  // Validate inputs
  let isValid = true;

  // Check for empty fields
  if (!nameInput.value.trim()) {
    document.getElementById('name-error').textContent = 'Name is required';
    document.getElementById('name-error').classList.remove('hidden');
    isValid = false;
  }

  // Check for duplicate names
  const goals = getGoals();
  if (goals.some(g => g.name === nameInput.value.trim())) {
    document.getElementById('name-error').textContent = 'Goal with this name already exists';
    document.getElementById('name-error').classList.remove('hidden');
    isValid = false;
  }

  // Check for maximum goals
  if (goals.length >= 4) {
    alert('Maximum 4 goals allowed. Please delete a goal before creating a new one.');
    isValid = false;
  }

  // Similar validation for other fields

  if (isValid) {
    try {
      addGoal({
        name: nameInput.value.trim(),
        category: categoryInput.value.trim(),
        targetAmount: parseFloat(targetInput.value),
        endDate: dateInput.value,
      });

      // Reset form and refresh goals list
      this.reset();
      displayGoals();
    } catch (error) {
      alert(error.message);
    }
  }
});

// Function to display goals
function displayGoals() {
  const goalsContainer = document.getElementById('goals-list');
  const goals = getGoals();

  goalsContainer.innerHTML = '';

  goals.forEach(goal => {
    const goalElement = document.createElement('div');
    goalElement.className = 'goal-item p-4 border rounded-lg mb-4';

    // Calculate progress percentage
    const progress = Math.min(100, (goal.currentAmount / goal.targetAmount) * 100);

    // Check if goal is overdue
    const isOverdue = new Date(goal.endDate) < new Date() && !goal.isCompleted;

    goalElement.innerHTML = `
      <div class="flex justify-between items-center mb-2">
        <h3 class="font-bold">${goal.name}</h3>
        <button class="delete-goal text-red-500" data-id="${goal.id}">Delete</button>
      </div>
      <div class="text-sm mb-1">${goal.category} | Target: $${goal.targetAmount}</div>
      <div class="text-sm mb-2 ${isOverdue ? 'text-red-500' : ''}">Deadline: ${new Date(goal.endDate).toLocaleDateString()} ${isOverdue ? '(Overdue)' : ''}</div>
      <div class="relative h-2 bg-gray-200 rounded-full">
        <div class="absolute top-0 left-0 h-full bg-blue-600 rounded-full" style="width: ${progress}%"></div>
      </div>
      <div class="text-right text-sm mt-1">${progress.toFixed(1)}%</div>
    `;

    goalsContainer.appendChild(goalElement);
  });

  // Add event listeners for delete buttons
  document.querySelectorAll('.delete-goal').forEach(button => {
    button.addEventListener('click', function() {
      const goalId = parseInt(this.getAttribute('data-id'));
      deleteGoal(goalId);
      displayGoals();
    });
  });
}

// Function to delete a goal
function deleteGoal(goalId) {
  const goals = getGoals();
  const updatedGoals = goals.filter(goal => goal.id !== goalId);
  saveGoals(updatedGoals);
}
```

