// Function to open progress modal
function openProgressModal(goalId, goalName) {
  document.getElementById('progress-goal-id').value = goalId;
  document.getElementById('progress-goal-name').textContent = `Add Progress for ${goalName}`;
  document.getElementById('progress-amount').value = '';
  document.getElementById('progress-modal').classList.remove('hidden');
}

// Event listener for progress form submission
document.getElementById('progress-form').addEventListener('submit', function(e) {
  e.preventDefault();
  const goalId = parseInt(document.getElementById('progress-goal-id').value);
  const amount = parseFloat(document.getElementById('progress-amount').value);

  if (amount <= 0) {
    alert('Please enter a positive amount');
    return;
  }

  addProgress(goalId, amount);
  document.getElementById('progress-modal').classList.add('hidden');
  displayGoals();
});

// Event listener for cancel button
document.getElementById('cancel-progress').addEventListener('click', function() {
  document.getElementById('progress-modal').classList.add('hidden');
});

// Function to add progress to a goal
function addProgress(goalId, amount) {
  const goals = getGoals();
  const goalIndex = goals.findIndex(goal => goal.id === goalId);

  if (goalIndex === -1) return;

  const goal = goals[goalIndex];
  const newAmount = goal.currentAmount + amount;
  const progressEntry = {
    amount,
    date: new Date().toISOString(),
    newTotal: newAmount
  };

  goal.currentAmount = newAmount;
  goal.progressHistory.push(progressEntry);

  // Check if goal is completed
  if (newAmount >= goal.targetAmount && !goal.isCompleted) {
    goal.isCompleted = true;
    goal.completedAt = new Date().toISOString();
    celebrateGoalCompletion(goal.name);
  }

  goals[goalIndex] = goal;
  saveGoals(goals);

  // Update progress storage for statistics
  const progress = JSON.parse(localStorage.getItem('marcus_progress') || '[]');
  progress.push(progressEntry);
  localStorage.setItem('marcus_progress', JSON.stringify(progress));
}

// Function to show progress history
function showProgressHistory(goalId) {
  const goals = getGoals();
  const goal = goals.find(g => g.id === goalId);

  if (!goal) return;

  let historyHTML = `<h3 class="text-lg font-bold mb-2">Progress History for ${goal.name}</h3>`;

  if (goal.progressHistory.length === 0) {
    historyHTML += '<p>No progress entries yet.</p>';
  } else {
    historyHTML += '<ul class="space-y-2">';
    goal.progressHistory.forEach(entry => {
      historyHTML += `
        <li class="border-b pb-2">
          <div class="flex justify-between">
            <span>$${entry.amount.toFixed(2)}</span>
            <span class="text-gray-500">${new Date(entry.date).toLocaleDateString()}</span>
          </div>
          <div class="text-sm text-right">Total: $${entry.newTotal.toFixed(2)}</div>
        </li>
      `;
    });
    historyHTML += '</ul>';
  }

  // Show history in a modal or other UI element
  alert(historyHTML); // Replace with better UI in a real implementation
}

// Function to celebrate goal completion
function celebrateGoalCompletion(goalName) {
  // Will be implemented in Task 7
  console.log(`Goal completed: ${goalName}`);
}
```

