```javascript
// Function to update dashboard
function updateDashboard() {
  const goals = getGoals();

  // Calculate statistics
  const totalSaved = goals.reduce((sum, goal) => sum + goal.currentAmount, 0);
  const activeGoals = goals.length;
  const completedGoals = goals.filter(goal => goal.isCompleted).length;

  // Calculate overall progress
  let overallProgress = 0;
  if (activeGoals > 0) {
    const totalTargets = goals.reduce((sum, goal) => sum + goal.targetAmount, 0);
    overallProgress = totalTargets > 0 ? (totalSaved / totalTargets) * 100 : 0;
  }

  // Update statistics display
  document.getElementById('total-saved').textContent = `$${totalSaved.toFixed(2)}`;
  document.getElementById('active-goals').textContent = `${activeGoals}/4`;
  document.getElementById('completed-goals').textContent = completedGoals;
  document.getElementById('overall-progress').textContent = `${overallProgress.toFixed(1)}%`;

  // Display goals
  const goalsContainer = document.getElementById('dashboard-goals');
  goalsContainer.innerHTML = '';

  if (goals.length === 0) {
    goalsContainer.innerHTML = `
      <div class="text-center py-8 text-gray-500">
        <p>You don't have any savings goals yet.</p>
        <a href="goals.html" class="text-blue-600 font-medium">Create your first goal</a>
      </div>
    `;
    return;
  }

  goals.forEach(goal => {
    const progress = Math.min(100, (goal.currentAmount / goal.targetAmount) * 100);
    const isOverdue = new Date(goal.endDate) < new Date() && !goal.isCompleted;

    const goalElement = document.createElement('div');
    goalElement.className = 'goal-card p-4 border rounded-lg';
    goalElement.innerHTML = `
      <div class="flex justify-between items-center mb-2">
        <h3 class="font-bold">${goal.name}</h3>
        <span class="text-sm ${isOverdue ? 'text-red-500' : 'text-gray-500'}">Due: ${new Date(goal.endDate).toLocaleDateString()} ${isOverdue ? '(Overdue)' : ''}</span>
      </div>
      <div class="flex justify-between text-sm mb-1">
        <span>${goal.category}</span>
        <span>$${goal.currentAmount.toFixed(2)} / $${goal.targetAmount.toFixed(2)}</span>
      </div>
      <div class="relative h-2 bg-gray-200 rounded-full">
        <div class="absolute top-0 left-0 h-full bg-blue-600 rounded-full" style="width: ${progress}%"></div>
      </div>
      <div class="text-right text-sm mt-1">${progress.toFixed(1)}%</div>
    `;

    goalsContainer.appendChild(goalElement);
  });
}

// Call updateDashboard when the page loads
document.addEventListener('DOMContentLoaded', function() {
  if (window.location.pathname.includes('index.html') || window.location.pathname === '/') {
    updateDashboard();
  }
});
```

