JavaScript for profile page:
```javascript
// Function to load user statistics
function loadUserStats() {
  const goals = getGoals();
  const progress = JSON.parse(localStorage.getItem('marcus_progress') || '[]');

  // Calculate statistics
  const totalSaved = goals.reduce((sum, goal) => sum + goal.currentAmount, 0);
  const goalsCompleted = goals.filter(goal => goal.isCompleted).length;
  const activeGoals = goals.length - goalsCompleted;
  const contributions = progress.length;

  // Update statistics display
  document.getElementById('profile-total-saved').textContent = `$${totalSaved.toFixed(2)}`;
  document.getElementById('profile-goals-completed').textContent = goalsCompleted;
  document.getElementById('profile-active-goals').textContent = activeGoals;
  document.getElementById('profile-contributions').textContent = contributions;

  // Update stats in localStorage
  const stats = {
    totalSaved,
    goalsCompleted,
    activeGoals,
    contributions,
    lastUpdated: new Date().toISOString()
  };

  localStorage.setItem('marcus_stats', JSON.stringify(stats));
}

// Function to load user settings
function loadUserSettings() {
  const settings = JSON.parse(localStorage.getItem('marcus_settings') || '{}');

  // Set default settings if not exists
  const defaultSettings = {
    displayName: 'User',
    notifications: {
      progress: true,
      goals: true,
      reminders: false
    },
    currency: 'USD'
  };

  const userSettings = { ...defaultSettings, ...settings };

  // Populate form with settings
  document.getElementById('display-name').value = userSettings.displayName;
  document.getElementById('notify-progress').checked = userSettings.notifications.progress;
  document.getElementById('notify-goals').checked = userSettings.notifications.goals;
  document.getElementById('notify-reminders').checked = userSettings.notifications.reminders;
  document.getElementById('currency').value = userSettings.currency;

  return userSettings;
}

// Function to save user settings
function saveUserSettings(event) {
  event.preventDefault();

  const settings = {
    displayName: document.getElementById('display-name').value.trim() || 'User',
    notifications: {
      progress: document.getElementById('notify-progress').checked,
      goals: document.getElementById('notify-goals').checked,
      reminders: document.getElementById('notify-reminders').checked
    },
    currency: document.getElementById('currency').value
  };

  localStorage.setItem('marcus_settings', JSON.stringify(settings));

