// Initialize application
function initializeApp() {
  // Initialize storage
  initializeStorage();

  // Set up navigation highlighting
  setupNavigation();

  // Load page-specific content
  loadPageContent();

  // Apply responsive layout
  handleResponsiveLayout();
}

// Function to set up navigation highlighting
function setupNavigation() {
  const currentPage = window.location.pathname;
  const navItems = document.querySelectorAll('.nav-item');

  navItems.forEach(item => {
    const href = item.getAttribute('href');
    if (currentPage.includes(href) || (href === 'index.html' && (currentPage === '/' || currentPage.endsWith('/')))) {
      item.classList.add('active', 'text-blue-600');
    } else {
      item.classList.remove('active', 'text-blue-600');
    }
  });
}

// Function to load page-specific content
function loadPageContent() {
  const currentPage = window.location.pathname;

  if (currentPage.includes('index.html') || currentPage === '/' || currentPage.endsWith('/')) {
    updateDashboard();
  } else if (currentPage.includes('goals.html')) {
    displayGoals();
  } else if (currentPage.includes('friends.html')) {
    displayFriends();
    populateShareGoals();
  } else if (currentPage.includes('profile.html')) {
    loadUserStats();
    loadUserSettings();
  }
}

// Call initializeApp when the DOM is loaded
document.addEventListener('DOMContentLoaded', initializeApp);
